	  DropdownButtonBasic: "const BUTTONS = ['Default', 'Primary', 'Success', 'Info', 'Warning', 'Danger', 'Link'];\r\n\r\nfunction renderDropdownButton(title, i) {\r\n  return (\r\n    <DropdownButton bsStyle={title.toLowerCase()} title={title} key={i} id={`dropdown-basic-${i}`}>\r\n      <MenuItem eventKey=\"1\">Action</MenuItem>\r\n      <MenuItem eventKey=\"2\">Another action</MenuItem>\r\n      <MenuItem eventKey=\"3\" active>Active Item</MenuItem>\r\n      <MenuItem divider />\r\n      <MenuItem eventKey=\"4\">Separated link</MenuItem>\r\n    </DropdownButton>\r\n  );\r\n}\r\n\r\nconst buttonsInstance = (\r\n  <ButtonToolbar>{BUTTONS.map(renderDropdownButton)}</ButtonToolbar>\r\n);\r\n\r\nReactDOM.render(buttonsInstance, mountNode);\r\n",
	  DropdownButtonCustom: "\r\nconst dropdownInstance = (\r\n  <ButtonToolbar>\r\n    <Dropdown id=\"dropdown-custom-1\">\r\n      <Dropdown.Toggle>\r\n        <Glyphicon glyph=\"star\" />\r\n        Pow! Zoom!\r\n      </Dropdown.Toggle>\r\n      <Dropdown.Menu className=\"super-colors\">\r\n        <MenuItem eventKey=\"1\">Action</MenuItem>\r\n        <MenuItem eventKey=\"2\">Another action</MenuItem>\r\n        <MenuItem eventKey=\"3\" active>Active Item</MenuItem>\r\n        <MenuItem divider />\r\n        <MenuItem eventKey=\"4\">Separated link</MenuItem>\r\n      </Dropdown.Menu>\r\n    </Dropdown>\r\n\r\n    <Dropdown id=\"dropdown-custom-2\">\r\n      <Button bsStyle=\"info\">\r\n        mix it up style-wise\r\n      </Button>\r\n      <Dropdown.Toggle bsStyle=\"success\"/>\r\n      <Dropdown.Menu className=\"super-colors\">\r\n        <MenuItem eventKey=\"1\">Action</MenuItem>\r\n        <MenuItem eventKey=\"2\">Another action</MenuItem>\r\n        <MenuItem eventKey=\"3\" active>Active Item</MenuItem>\r\n        <MenuItem divider />\r\n        <MenuItem eventKey=\"4\">Separated link</MenuItem>\r\n      </Dropdown.Menu>\r\n    </Dropdown>\r\n\r\n  </ButtonToolbar>\r\n);\r\n\r\nReactDOM.render(dropdownInstance, mountNode);\r\n",
	  DropdownButtonCustomMenu: "\r\nclass CustomMenu extends React.Component {\r\n\r\n  constructor(...args) {\r\n    super(...args);\r\n    this.state = { value: '' };\r\n    this.onChange = e => this.setState({ value: e.target.value });\r\n  }\r\n\r\n  render() {\r\n    let { className, ...props } = this.props;\r\n\r\n    return (\r\n      <div\r\n        className={\"dropdown-menu\"}\r\n        style={{ padding: ''}}\r\n      >\r\n        <input\r\n          ref={input => this.input = input}\r\n          type=\"text\"\r\n          className=\"form-control\"\r\n          placeholder=\"type to filter...\"\r\n          onChange={this.onChange}\r\n          value={this.state.value}\r\n        />\r\n        <ul className=\"list-unstyled\">\r\n          { this.filterChildren() }\r\n        </ul>\r\n      </div>\r\n    );\r\n  }\r\n\r\n  filterChildren() {\r\n    let { children } = this.props;\r\n    let { value } = this.state;\r\n    let filtered = [];\r\n\r\n    let matches = child => child.props.children.indexOf(value) !== -1;\r\n\r\n    React.Children.forEach(children, child => {\r\n      if (!value.trim() || matches(child)) {\r\n        filtered.push(child);\r\n      }\r\n    });\r\n\r\n    return filtered;\r\n  }\r\n\r\n  focusNext() {\r\n    let input = React.findDOMNode(this.input);\r\n\r\n    if (input) {\r\n      input.focus();\r\n    }\r\n  }\r\n}\r\n\r\nlet preventDefault = e => e.preventDefault();\r\n\r\nlet dropdownExample = (\r\n    <Dropdown id=\"dropdown-custom-menu\">\r\n      <a href=\"#\" bsRole=\"toggle\" onClick={preventDefault}>\r\n        custom Toggle\r\n      </a>\r\n\r\n      <CustomMenu bsRole=\"menu\">\r\n        <MenuItem eventKey=\"1\">Red</MenuItem>\r\n        <MenuItem eventKey=\"2\">Blue</MenuItem>\r\n        <MenuItem eventKey=\"3\" active>Orange</MenuItem>\r\n        <MenuItem eventKey=\"1\">Red-Orange</MenuItem>\r\n      </CustomMenu>\r\n    </Dropdown>\r\n  );\r\n\r\nReactDOM.render(dropdownExample, mountNode);\r\n",
	  SplitButtonBasic: "const BUTTONS = ['Default', 'Primary', 'Success', 'Info', 'Warning', 'Danger'];\r\n\r\nfunction renderDropdownButton(title, i) {\r\n  return (\r\n    <SplitButton bsStyle={title.toLowerCase()} title={title} key={i} id={`split-button-basic-${i}`}>\r\n      <MenuItem eventKey=\"1\">Action</MenuItem>\r\n      <MenuItem eventKey=\"2\">Another action</MenuItem>\r\n      <MenuItem eventKey=\"3\">Something else here</MenuItem>\r\n      <MenuItem divider />\r\n      <MenuItem eventKey=\"4\">Separated link</MenuItem>\r\n    </SplitButton>\r\n  );\r\n}\r\n\r\nconst buttonsInstance = (\r\n  <ButtonToolbar>{BUTTONS.map(renderDropdownButton)}</ButtonToolbar>\r\n);\r\n\r\nReactDOM.render(buttonsInstance, mountNode);\r\n",
